import { useState } from "react";
import EventCard from "../../components/EventCard";
import Select from "../../components/Select";
import { useData } from "../../contexts/DataContext";
import Modal from "../Modal";
import ModalEvent from "../ModalEvent";

import "./style.css";

const PER_PAGE = 9; // nombre d'éléments sur la page

const EventList = () => {
  // gestion de l'état
  const { data, error } = useData(); // initialise les états data et error en utilisant le hook useData
  const [type, setType] = useState(null); // initialise l'état type en utilisant useState
  const [currentPage, setCurrentPage] = useState(1); // initialise l'état de la page actuelle en utilisant useState

  // filtrer les événements en fonction du type et de la page actuelle
  const filteredEvents = (
    (type ? data?.events.filter(event => event.type === type) : data?.events) || []
  )
    .slice((currentPage - 1) * PER_PAGE, currentPage * PER_PAGE);

  // fonction pour changer le filtre type d'événement
  const changeType = (evtType) => {
    setCurrentPage(1); // réinitialiser la page actuelle à 1
    setType(evtType); // définir le filtre du type d'événement
  };

  // calcul du nombre de pages en fonction des événements filtrés
  const pageNumber = Math.ceil((data?.events.length || 0) / PER_PAGE);

  // obtenir la liste des types d'événement uniques
  const typeList = new Set(data?.events.map((event) => event.type));

  return (
    <>
      {error && <div>An error occurred</div>}
      {data === null ? (
        "Loading..."
      ) : (
        <>
          <h3 className="SelectTitle">Catégories</h3>
          <Select
            selection={Array.from(typeList)}
            onChange={(value) => changeType(value)}
          />
          <div id="events" className="ListContainer">
            {filteredEvents.map((event) => (
              <Modal key={event.id} Content={<ModalEvent event={event} />}>
                {({ setIsOpened }) => (
                  <EventCard
                    onClick={() => setIsOpened(true)}
                    imageSrc={event.imageSrc} // Assurez-vous que la propriété imageSrc est correctement définie dans vos données d'événement
                    title={event.title} // Assurez-vous que la propriété title est correctement définie dans vos données d'événement
                    date={new Date(event.date)}
                    label={event.type}
                  />
                )}
              </Modal>
            ))}
          </div>
          <div className="Pagination">
            {[...Array(pageNumber)].map((_, n) => (
              <a key={n} href="#events" onClick={() => setCurrentPage(n + 1)}>
                {n + 1}
              </a>
            ))}
          </div>
        </>
      )}
    </>
  );
};

export default EventList;
