import { useState } from "react";
import EventCard from "../../components/EventCard";
import Select from "../../components/Select";
import { useData } from "../../contexts/DataContext";
import Modal from "../Modal";
import ModalEvent from "../ModalEvent";

import "./style.css";

const PER_PAGE = 9;

const EventList = () => {
  const { data, error } = useData();
  const [type, setType] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);

  // Filtrer les événements en fonction du type sélectionné
  const filteredEvents = type ? data?.events.filter(event => event.type === type) : data?.events || [];

  // Mettre à jour le type lorsque l'utilisateur change la sélection
  const changeType = (eventType) => {
    setCurrentPage(1);
    setType(eventType);
  };

  // Calculer le nombre total de pages en fonction du nombre d'événements filtrés
  const pageNumber = Math.ceil(filteredEvents.length / PER_PAGE);

  return (
    <>
      {error && <div>An error occurred</div>}
      {data === null ? (
        "Loading"
      ) : (
        <>
          <h3 className="SelectTitle">Catégories</h3>
          <Select
            selection={['Tous les types', ...new Set(data?.events.map(event => event.type))]}
            onChange={(value) => changeType(value === 'Tous les types' ? null : value)}
          />
          <div id="events" className="ListContainer">
            {filteredEvents.slice((currentPage - 1) * PER_PAGE, currentPage * PER_PAGE).map((event) => (
              <Modal key={event.id} Content={<ModalEvent event={event} />}>
                {({ setIsOpened }) => (
                  <EventCard
                    onClick={() => setIsOpened(true)}
                    imageSrc={event.cover}
                    title={event.title}
                    date={new Date(event.date)}
                    label={event.type}
                  />
                )}
              </Modal>
            ))}
          </div>
          <div className="Pagination">
            {[...Array(pageNumber)].map((_, n) => (
              <a key={n} href="#events" onClick={() => setCurrentPage(n + 1)}>
                {n + 1}
              </a>
            ))}
          </div>
        </>
      )}
    </>
  );
};

export default EventList;
