import React from 'react';
import EventCard from './EventCard'; // Assurez-vous que le chemin d'importation est correct

const LatestEvent = () => {
  // Supposons que vous ayez une liste d'événements stockée dans un état local
  const [events, setEvents] = React.useState([
    // Exemple d'événement
    {
      id: 1,
      imageSrc: 'url_de_votre_image',
      imageAlt: 'Texte de l\'image',
      date: new Date(),
      title: 'Titre de l\'événement',
      label: 'Étiquette de l\'événement',
    },
    // Ajoutez d'autres événements ici...
  ]);

  // Triez les événements par date pour obtenir le dernier événement en premier
  const sortedEvents = events.sort((a, b) => b.date - a.date);

  // Récupérez le dernier événement
  const latestEvent = sortedEvents[0];

  return (
    <div>
      {latestEvent && (
        <EventCard
          imageSrc={latestEvent.imageSrc}
          imageAlt={latestEvent.imageAlt}
          date={latestEvent.date}
          title={latestEvent.title}
          label={latestEvent.label}
        />
      )}
    </div>
  );
};

export default LatestEvent;
